        DogStore() { }
        ~DogStore() {}
        Pet * getOnePet(string name) {
            pInst = new Dog(name);
            return pInst;
        }
    }
    class CatStore: public Store  {
        CatStore() {}
        ~CatStore() {}
        Pet * getOnePet(string name) {
            pInst = new Cat(name);
            return pInst;
        }
    }
