        this.type = type;
        this.name = name;
    }
    Animal.prototype.display = function() {
            alert("I am a " + this.type + ",my name is " + this.name);
    };

    function Dog(color) {
        this.color = color;
        this.legNum = 4;
    }
    Dog.prototype = new Animal('dog','wangcai');

    var dog = new Dog('black');
    dog.display();
    // I am a dog，my name is wangcai

