    class XMan {
        Person m_tPerson;
        Skill m_skillList = [];

        XMan(Person person) {
            m_tPerson = person;
        }
        ~XMan() {m_skillList = [];}

        void addSkill(Skill skill) {
            m_skillList.push(skill);
        }
        void display() {
            m_tPerson.display();
            cout << "" << endl;
            for(int index=0;index < m_skillList.length;index++) {
                m_skillList[index].display();
