    })
    export class ReactiveFormComponent implements OnInit {
      myForm : FormGroup;
      sexs : any[] = [{'name':'','value':'famale'},{'name':'','value':'male'}];
      likes : any[] = [{'name':'','value':'Watch Tv','isChecked':false},
                        {'name':'','value':'Book','isChecked':false}];
      selectedLikes : any[] = [];
      baocuns : any[] = [{'name':'','value':'Yes','isChecked':false},{'name':'','value':'No','isChecked':false}];
      skillList : any[] = [{'value':'Java'},{'value':'Scala'}];

      constructor(private fb : FormBuilder) {
        this.myForm = this.fb.group({
          name: ['Jack',
                 [Validators.required,Validators.minLength(4),forbiddenNameValidator(/[@\$&0-9]/)]],
          sex: ['male',Validators.required],
          shengri: ['1990-01-01',Validators.required],
          password: ['',Validators.required],
          address: this.fb.array([]),
          like: [],
          baocun:'',
          skills: this.fb.array([])
        });
      }

      ngOnInit() {
        this.initAdressList();
        this.initSkillList();

      }

      get name() { return this.myForm.get('name');}

      get password() { return this.myForm.get('password');}

      selectLikes(flag: any,value : any) {
        if(flag.target.checked) {
          this.selectedLikes.push(value);
        } else {
          this.selectedLikes = this.selectedLikes.filter((like) => {
            return like !== value;
